    handleThreadCommentForm: function(threadId) { const form = document.getElementById('comment-form'); if (!form) { setTimeout(() => this.handleThreadCommentForm(threadId), 100); return; } const commentTextInput = document.getElementById('comment-text-input'); const counter = document.getElementById('comment-char-counter'); if (!commentTextInput || !counter) return; const MAX_COMMENT_LENGTH = 100; const updateCounter = () => { const currentLength = commentTextInput.value.length; counter.textContent = `${currentLength} / ${MAX_COMMENT_LENGTH}`; counter.classList.toggle('error', currentLength > MAX_COMMENT_LENGTH); }; commentTextInput.addEventListener('input', updateCounter); updateCounter(); form.addEventListener('submit', (e) => { e.preventDefault(); const commentText = commentTextInput.value.trim(); if (commentText.length > MAX_COMMENT_LENGTH) { alert(`ã‚³ãƒ¡ãƒ³ãƒˆãŒé•·ã™ãã¾ã™ï¼ˆ${MAX_COMMENT_LENGTH}æ–‡å­—ä»¥å†…ï¼‰ã€‚`); return; } if (commentText) { const newComment = { author: this.currentUser.name, text: commentText, date: new Date().toISOString() }; if (!this.threadComments[threadId]) { this.threadComments[threadId] = []; } this.threadComments[threadId].push(newComment); localStorage.setItem('threadComments', JSON.stringify(this.threadComments)); const thread = this.allThreads.find(t => t.id === threadId); if (thread) { thread.commentCount = (thread.commentCount || 0) + 1; localStorage.setItem('allThreads', JSON.stringify(this.allThreads)); } this.renderThreadDetail(threadId); this.handleThreadCommentForm(threadId); } }); },
